import React from 'react';

class RegionSelect extends React.Component {
	constructor(props) {
		super(props);
		this.state = {data: {}};
	}

	componentDidMount() {
		fetch('/api/regions').then((res) => res.json()).then((data) => this.setState({ data }));
	}

	render() {
		if (this.state.data) {
			console.log(this.state.data.regions)
            const array = [this.state.data.regions]
            console.log(array)
			return (
				<select>
                    {array.map(reg=> {return <option value={reg}>{reg}</option>})}
                </select>
			);
		} 
	}
}

export default RegionSelect;
