import React from 'react';

class DistrictSelect extends React.Component {
	constructor(props) {
		super(props);
		this.state = {data: {}};
	}

    handleChange = (e) => {
        console.log(e.target.value)
        this.props.onChange(e)
    }

	componentDidMount() {
        console.log(this.props)
        const region = this.props.region;
        console.log(region)
        if(region.length > 0){
            fetch(`/api/districts/${region}`).then((res) => res.json()).then((data) => this.setState({ data }));
        } else {
            fetch('/api/districts').then((res) => res.json()).then((data) => this.setState({ data }));
        }
		
	}

	render() {
		if (this.state.data.districts) {
            const array = this.state.data.districts
			return (
				<select onChange={(e)=> this.handleChange(e)}>
                    {array.map((reg, i) => {return <option key={i} value={reg}>{reg}</option>})}
                </select>
			);
		} return ''
	}
}

export default DistrictSelect;
